import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,Text,View,TextInput,Button,Image,Pressable,Alert,FlatList,TouchableOpacity} from "react-native";
import React, { useEffect, useState } from "react";
import faviconImage from "../assets/favicon.png";

import { useNavigation } from "@react-navigation/native";

const bookList = [
    {
        id: 1,
        name: "Book",
        img: faviconImage,
    },
    {
        id: 2,
        name: "Book",
        img: faviconImage,
    },
    {
        id: 3,
        name: "Book",
        img: faviconImage,
    },
    {
        id: 4,
        name: "Book",
        img: faviconImage,
    },
    {
        id: 5,
        name: "Book",
        img: faviconImage,
    },
    {
        id: 6,
        name: "Book",
        img: faviconImage,
    },
];

export default function Books() {
    const navigation = useNavigation();
        

    return(
        <View>
            <TouchableOpacity>
                <Text style={styles.searchbar}>Search</Text>
            </TouchableOpacity>

            <FlatList
              data = {bookList}
              renderItem={({item})=>(
                <TouchableOpacity style={styles.bookSection}>
                    <View>
                        <Image>
                        style={{
                  width: 60,
                  height: 60,
                  borderRadius: 150 / 2,
                  overflow: "hidden",
                  borderWidth: 3,
                  borderColor: "red",
                }}
                source={item.img}
                        </Image>
                    </View>
                </TouchableOpacity>
              )}
              keyExtractor={(item) => item.id}
              />
        </View>
    );
}

const styles = StyleSheet.create({
    searchbar: {
        fontSize: 30,
        marginTop: 20,
        textAlign: "center",
    },
    bookSection: {

    }
})
