import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,Text,View,TextInput,Button,Image,Pressable,Alert,FlatList,TouchableOpacity} from "react-native";
import React, { useEffect, useState } from "react";
import faviconImage from "../assets/favicon.png";
import ubook1 from "../assets/ubook1.jpeg";

import { useNavigation } from "@react-navigation/native";

const bookList = [
    {
        id: 1,
        name: "Book",
        img: ubook1,
    },
    {
        id: 2,
        name: "Book",
        img: ubook1,
    },
    {
        id: 3,
        name: "Book",
        img: ubook1,
    },
    {
        id: 4,
        name: "Book",
        img: ubook1,
    },
    {
        id: 5,
        name: "Book",
        img: ubook1,
    },
    {
        id: 6,
        name: "Book",
        img: ubook1,
    },
];

export default function Books() {
    const navigation = useNavigation();
        

    return(
        <View>
            {/* <TouchableOpacity>
                <Text style={styles.searchbar}>Search</Text>
            </TouchableOpacity> */}
            <TextInput
        numberOfLines={4}
        maxLength={40}
       // onChangeText={text => onChangeText(text)}
        style={styles.searchbar}
        placeholder="Search"
        
            />

            <FlatList
              data = {bookList}
              horizontal={false} // To display items in a row when true
              numColumns={3}
              renderItem={({item})=>(
                <TouchableOpacity style={styles.topSection}>
                    <View style={styles.bookSection}>
                        <Image 
                        style={styles.imageSection}
                        source={item.img}
                        />
                        <Text>{item.name}</Text>
                    </View>
                </TouchableOpacity>
              )}
              keyExtractor={(item) => item.id}
              />
        </View>
    );
}

const styles = StyleSheet.create({
    searchbar: {
        flex: 1,
        fontSize: 30,
        textAlign: "center",
        justifyContent: "center",
        width: "max", 
        height: 60, 
        borderColor: 'gray', 
        borderWidth: 1, 
        backgroundColor: 'white', 
        marginTop: 20, 
        borderRadius: 50, 
        color: 'black'
    },
    topSection: {
        flex: 1,
        justifyContent: "space-around",
    },
    bookSection: {
        flex: 0.3,
        marginBottom: 50,

    },
    imageSection: {
        width: 100,
        height: 150,
        overflow: "hidden",
    }
})
